import React, { useEffect, useState } from "react";
import axios from "../api/axios";

function ProductList() {
  const [products, setProducts] = useState([]);
  const [next, setNext] = useState(null);
  const [previous, setPrevious] = useState(null);

  const fetchProducts = async (url = "products/") => {
    const res = await axios.get(url);
    setProducts(res.data.results);
    setNext(res.data.next);
    setPrevious(res.data.previous);
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  return (
    <div className="p-8">
      <h1 className="text-2xl font-bold mb-4">Products</h1>
      <ul>
        {products.map((p) => (
          <li key={p.id} className="border p-2 my-2 rounded">
            {p.name} - ${p.price}
          </li>
        ))}
      </ul>
      <div className="flex justify-between mt-4">
        <button
          disabled={!previous}
          onClick={() => fetchProducts(previous.replace("http://127.0.0.1:8000/api/", ""))}
          className="bg-gray-300 p-2 rounded disabled:opacity-50"
        >
          Previous
        </button>
        <button
          disabled={!next}
          onClick={() => fetchProducts(next.replace("http://127.0.0.1:8000/api/", ""))}
          className="bg-blue-500 text-white p-2 rounded disabled:opacity-50"
        >
          Next
        </button>
      </div>
    </div>
  );
}

export default ProductList;
