import React from 'react';
import { Card } from 'react-bootstrap';

const ProductCard = ({ product }) => {
  const imageUrl = product.image_url || 'https://via.placeholder.com/300x200?text=No+Image';
  return (
    <Card className="h-100 shadow-sm">
      <Card.Img variant="top" src={imageUrl} style={{ height: '200px', objectFit: 'cover' }} />
      <Card.Body>
        <Card.Title className="mb-1">{product.name}</Card.Title>
        <Card.Subtitle className="mb-2 text-muted">{product.category}</Card.Subtitle>
        <Card.Text style={{ minHeight: '3rem' }}>
          {product.description ? (product.description.length > 100 ? product.description.substring(0, 100) + '...' : product.description) : 'No description'}
        </Card.Text>
        <div className="fw-bold">${parseFloat(product.price).toFixed(2)}</div>
      </Card.Body>
    </Card>
  );
};

export default ProductCard;
