const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001/api';

// Generic API request function
const apiRequest = async (endpoint, options = {}) => {
  const token = localStorage.getItem('token');
  
  const config = {
    headers: {
      'Content-Type': 'application/json',
      ...(token && { 'Authorization': `Bearer ${token}` }),
      ...options.headers,
    },
    ...options,
  };

  if (config.body && typeof config.body === 'object') {
    config.body = JSON.stringify(config.body);
  }

  const response = await fetch(`${API_BASE_URL}${endpoint}`, config);
  
  if (!response.ok) {
    throw new Error(`API error: ${response.status}`);
  }

  return response.json();
};

// Product API functions
export const getProducts = () => apiRequest('/products');
export const getProduct = (id) => apiRequest(`/products/${id}`);
export const createProduct = (product) => apiRequest('/products', { method: 'POST', body: product });

// Order API functions
export const createOrder = (order) => apiRequest('/orders', { method: 'POST', body: order });
export const getOrders = () => apiRequest('/orders');
export const getOrder = (id) => apiRequest(`/orders/${id}`);

// Cart API functions (for persistent cart)
export const saveCart = (cart) => apiRequest('/cart', { method: 'POST', body: cart });
export const getCart = () => apiRequest('/cart');
